@import url('https://fonts.googleapis.com/css2?family=Noto+Color+Emoji&display=swap');

html, body, h1, h2, h3, h4, h5, h6, div, p, input, textarea, span, label, section, article, header, footer, ul, li, ol {
    font-family: Inter, "源柔ゴシックL", "Noto Color Emoji";
}

pre code, pre, code, backquote, code span {
    font-family: HackGen;
}

