}

#UniqueMessageBody pre * {
    font-family: HackGen;
}

@import url('https://fonts.googleapis.com/css2?family=Noto+Color+Emoji&display=swap');

#info-detail span {
    font-family: Inter, "源柔ゴシックL", "源真ゴシック", "Noto Color Emoji";
