@import url('https://fonts.googleapis.com/css2?family=Noto+Color+Emoji&display=swap');

:root {
    --sds-font-family-01: Inter, "源柔ゴシックL", "源真ゴシック", "Noto Color Emoji"!important;
    --sds-font-family-monospace: HackGen!important;
}

.search__input--adv {
    font-family: Inter, "源柔ゴシックL", "源真ゴシック", "Noto Color Emoji";
}

textarea#read-only-cursor-text-area {
    font-family: HackGen, ui-monospace,SFMono-Regular,"SF Mono",Menlo,Consolas,"Liberation Mono",monospace !important;
}

@import url('https://fonts.googleapis.com/css2?family=Noto+Color+Emoji&display=swap');

.font-display, .prose :where(h2):not(:where([class~="not-prose"] *)), .font-sans {
    font-family: Inter, "源柔ゴシックL", "源真ゴシック", "Noto Color Emoji";
}

div#root {
    font-family: "Kosugi Maru"    
}

h1.markdown__heading {
    font-size: 24px;
    border-bottom: 1px solid rgba(119, 119, 119, .5);
}

h2.markdown__heading {
    font-size: 22px;
    border-bottom: 1px solid rgba(119, 119, 119, .5);
}

h3.markdown__heading {
    font-size: 20px;
}

h4.markdown__heading {
    font-size: 18px;
}

h5.markdown__heading {
    font-size: 16px;
}

h6.markdown__heading {
    font-size: 14px;
}

@import url('https://fonts.googleapis.com/css2?family=Noto+Color+Emoji&display=swap');

body, input, select, h1, h2, h3, h4, #wrapper input[type="text"], .modal input[type="text"], textarea.text_cf {
    font-family: Inter, HackGen, "源柔ゴシックL", "源真ゴシック", "Noto Color Emoji";
    letter-spacing: .02em;
}

#wrapper textarea.wiki-edit, .modal textarea.wiki-edit, textarea#custom_field_description {
    font-family: Inter, HackGen, "源柔ゴシックL", "源真ゴシック", "Noto Color Emoji";
    font-size: 16px;
    letter-spacing: .02em;
}

.tabular .wiki-preview, .tabular .jstTabs {
    font-size: 16px;
}

/*.tabular .wiki-preview p {
    padding-bottom: 0!important;
}*/

table.filecontent td.line-code pre {
    font-family: HackGen;
}

.wiki h1, .wiki h2, .wiki h3, .wiki h4 {
    margin: 0;
    margin-bottom: 1em;
}

.wiki h1:not(:first-of-type), .wiki h2:not(:first-of-type), .wiki h3:not(:first-of-type), .wiki h4:not(:first-of-type) {
    padding-top: 1em;
}

pre, code {
    font-family: HackGen;
}

:not(pre) > code {
    background-color: rgb(238, 238, 238, 0.7);
    padding: 3px;
    border-radius: 3px;
    margin: 0 3px;
}
