:root {
    --system-sans-serif: sans-serif;
    --system-monospace: monospace;
}

body, h1, h2, h3, h4, h5, h6 {
    font-family: var(--system-sans-serif);
}

pre, code {
    font-family: var(--system-monospace);
}

/* -----------------------------------
 * sans-serif
 * -----------------------------------*/
/* MSゴシック系 */
@font-face {
    font-family: 'ＭＳ Ｐゴシック';
    font-weight: auto;
    src: local("GenJyuuGothicL-Medium");
}

@font-face {
    font-family: 'MS PGothic';
    font-weight: auto;
    src: local("GenJyuuGothicL-Medium");
}

/* メイリオ系 */
